mapper.select:
SELECT
-- 级联表的字段 ,yiyuanxinxi.yiyuanxinxi_name as yiyuanxinxiName ,yiyuanxinxi.yiyuanxinxi_types as yiyuanxinxiTypes ,yiyuanxinxi.yiyuanxinxi_phone as yiyuanxinxiPhone ,yiyuanxinxi.address_types as addressTypes ,yiyuanxinxi.yiyuanxinxi_photo as yiyuanxinxiPhoto ,yiyuanxinxi.yiyuanxinxi_content as yiyuanxinxiContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_email as yonghuEmail ,yonghu.address_types as addressTypes ,yonghu.new_money as newMoney ,yonghu.yonghu_delete as yonghuDelete
FROM yiyuanxinxi_liuyan a left JOIN yiyuanxinxi yiyuanxinxi ON a.yiyuanxinxi_id = yiyuanxinxi.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.yiyuanxinxi_id = #{params.yiyuanxinxiId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.yiyuanxinxi_liuyan_text like CONCAT('%',#{params.yiyuanxinxiLiuyanText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.yiyuanxinxi_id IS NOT NULL
and yiyuanxinxi.yiyuanxinxi_name like CONCAT('%',#{params.yiyuanxinxiName},'%')
and yiyuanxinxi.yiyuanxinxi_types = #{params.yiyuanxinxiTypes}
and yiyuanxinxi.yiyuanxinxi_phone like CONCAT('%',#{params.yiyuanxinxiPhone},'%')
and yiyuanxinxi.address_types = #{params.addressTypes}
and yiyuanxinxi.yiyuanxinxi_content like CONCAT('%',#{params.yiyuanxinxiContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.address_types = #{params.addressTypes}
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
